@charset "UTF-8";
:root {
  --bg-light: #f8f9fa;
  --text-light: #212529;
  --accent-light: #007bff;
  --container-bg-light: #ffffff;
  --chat-header-bg-light: #007bff;
  --chat-header-text-light: white;
  --chat-footer-bg-light: #f9f9f9;
  --chat-messages-bg-light: #f9f9f9;
  --chat-input-bg-light: white;
  --chat-input-border-light: #ccc;
  --chat-bubble-bg-light: #007bff;
  --chat-bubble-text-light: white;
  --user-message-text-light: #007bff; /* Cor do texto da mensagem do usu√°rio n√£o √© usada, pois o fundo √© colorido */
  --support-message-text-light: #555;
  --bg-dark: #1a1a1a;
  --text-dark: #e9ecef;
  --accent-dark: #61dafb;
  --container-bg-dark: #2c2c2c;
  --chat-header-bg-dark: #0056b3;
  --chat-header-text-dark: #e9ecef;
  --chat-footer-bg-dark: #222;
  --chat-messages-bg-dark: #272727;
  --chat-input-bg-dark: #333;
  --chat-input-border-dark: #555;
  --chat-bubble-bg-dark: var(--accent-dark);
  --chat-bubble-text-dark: var(--bg-dark);
  --user-message-text-dark: var(--accent-dark); /* Cor do texto da mensagem do usu√°rio n√£o √© usada, pois o fundo √© colorido */
  --support-message-text-dark: #bbb;
  --font-primary: "Inter", -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
}

body {
  font-family: var(--font-primary);
  margin: 0;
  background-color: var(--bg-light);
  color: var(--text-light);
  transition: background-color 0.4s ease, color 0.4s ease;
  overflow-x: hidden;
}

body.dark-mode {
  background-color: var(--bg-dark);
  color: var(--text-dark);
}

.appsmith-widget-container {
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  min-height: 80vh;
  text-align: center;
}

.dark-mode-toggle-container {
  position: fixed;
  top: 20px;
  right: 20px;
  z-index: 1002;
}

.dark-mode-toggle {
  background-color: var(--container-bg-light);
  color: var(--text-light);
  border: 1px solid var(--text-light);
  padding: 10px 15px;
  border-radius: 25px;
  cursor: pointer;
  font-size: 0.9em;
  font-weight: 500;
  outline: none;
  transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease, transform 0.2s ease;
  display: flex;
  align-items: center;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
}

.dark-mode-toggle:hover {
  transform: translateY(-2px);
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.15);
}

body.dark-mode .dark-mode-toggle {
  background-color: var(--container-bg-dark);
  color: var(--text-dark);
  border: 1px solid var(--text-dark);
}

body.dark-mode .dark-mode-toggle:hover {
  background-color: var(--accent-dark);
  color: var(--bg-dark);
  border-color: var(--accent-dark);
}

.icon { /* Para o √≠cone do bot√£o de tema */
  margin-right: 8px;
  font-size: 1.1em;
}

.content-container {
  background-color: var(--container-bg-light);
  color: var(--text-light);
  padding: 40px 50px;
  border-radius: 15px;
  box-shadow: 0 8px 25px rgba(0, 0, 0, 0.08);
  max-width: 550px;
  width: 90%;
  transition: background-color 0.4s ease, color 0.4s ease, box-shadow 0.4s ease;
}

body.dark-mode .content-container {
  background-color: var(--container-bg-dark);
  color: var(--text-dark);
  box-shadow: 0 8px 25px rgba(255, 255, 255, 0.05);
}

.content-container h1 {
  color: var(--accent-light);
  margin-top: 0;
  margin-bottom: 25px;
  font-size: 2.8em;
  font-weight: 700;
  letter-spacing: -0.5px;
  transition: color 0.4s ease;
}

body.dark-mode .content-container h1 {
  color: var(--accent-dark);
}

.content-container p {
  font-size: 1.15em;
  line-height: 1.7;
  margin-bottom: 20px;
  font-weight: 400;
}

.ai-name {
  font-weight: 600;
  color: var(--accent-light);
  transition: color 0.4s ease;
}

body.dark-mode .ai-name {
  color: var(--accent-dark);
}

.highlight {
  font-size: 1.25em;
  font-weight: 500;
  margin-top: 30px;
  margin-bottom: 25px; /* Espa√ßo antes do novo input */
  color: var(--accent-light);
  transition: color 0.4s ease;
}

body.dark-mode .highlight {
  color: var(--accent-dark);
}

/* Novo container para input na p√°gina principal */
.main-page-chat-input-container {
  display: flex;
  align-items: center;
  width: 100%;
  margin-top: 10px; /* Espa√ßamento adicional */
  margin-bottom: 20px; /* Espa√ßamento abaixo */
}

#main-page-chat-input {
  flex-grow: 1;
  padding: 12px 18px;
  border: 1px solid var(--chat-input-border-light);
  border-radius: 25px; /* Bordas arredondadas */
  font-size: 1em;
  background-color: var(--chat-input-bg-light);
  color: var(--text-light);
  transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease;
  margin-right: 10px;
}

body.dark-mode #main-page-chat-input {
  background-color: var(--chat-input-bg-dark);
  color: var(--text-dark);
  border-color: var(--chat-input-border-dark);
}

#main-page-chat-input::placeholder {
  color: var(--text-light);
  opacity: 0.7;
}

body.dark-mode #main-page-chat-input::placeholder {
  color: var(--text-dark);
  opacity: 0.7;
}

#main-page-send-button {
  padding: 12px 15px;
  background-color: var(--accent-light);
  color: white;
  border: none;
  border-radius: 50%; /* Bot√£o circular */
  cursor: pointer;
  font-size: 1.1em; /* Tamanho do √≠cone */
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background-color 0.3s ease;
  width: 46px; /* Largura fixa para manter c√≠rculo */
  height: 46px; /* Altura fixa para manter c√≠rculo */
}

body.dark-mode #main-page-send-button {
  background-color: var(--accent-dark);
  color: var(--bg-dark);
}

#main-page-send-button:hover {
  opacity: 0.9;
}

/* --- Estilos do Chat Widget --- */
.chat-bubble {
  position: fixed;
  bottom: 20px;
  right: 20px;
  background-color: var(--chat-bubble-bg-light);
  color: var(--chat-bubble-text-light);
  width: 60px;
  height: 60px;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
  z-index: 1000;
  transition: background-color 0.3s ease, color 0.3s ease;
}

body.dark-mode .chat-bubble {
  background-color: var(--chat-bubble-bg-dark);
  color: var(--chat-bubble-text-dark);
}

.chat-bubble i {
  font-size: 24px;
}

.chat-widget {
  display: none;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: var(--container-bg-light);
  color: var(--text-light);
  z-index: 1001;
  flex-direction: column;
  transition: background-color 0.3s ease, color 0.3s ease;
}

body.dark-mode .chat-widget {
  background-color: var(--container-bg-dark);
  color: var(--text-dark);
}

.chat-header {
  background-color: var(--chat-header-bg-light);
  color: var(--chat-header-text-light);
  padding: 20px;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  font-size: 18px;
  transition: background-color 0.3s ease, color 0.3s ease;
  position: relative;
}

body.dark-mode .chat-header {
  background-color: var(--chat-header-bg-dark);
  color: var(--chat-header-text-dark);
}

.chat-header button#close-chat {
  background: none;
  border: none;
  color: var(--chat-header-text-light);
  font-size: 20px;
  font-weight: bold;
  cursor: pointer;
  padding: 5px;
  transition: color 0.3s ease;
  z-index: 1;
}

body.dark-mode .chat-header button#close-chat {
  color: var(--chat-header-text-dark);
}

.chat-header .chat-title {
  font-weight: bold;
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  max-width: calc(100% - 100px);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.messages-container {
  flex-grow: 1;
  padding: 20px;
  overflow-y: auto;
  background-color: var(--chat-messages-bg-light);
  transition: background-color 0.3s ease;
}

body.dark-mode .messages-container {
  background-color: var(--chat-messages-bg-dark);
}

.message {
  margin-bottom: 12px;
  padding: 8px 12px;
  border-radius: 10px;
  line-height: 1.4;
  max-width: 75%;
  width: -moz-fit-content;
  width: fit-content;
  overflow-wrap: break-word;
}

.user-message {
  background-color: var(--accent-light);
  color: white;
  margin-left: auto;
}

body.dark-mode .user-message {
  background-color: var(--accent-dark);
  color: var(--bg-dark);
}

.support-message {
  background-color: #e9ecef;
  color: var(--support-message-text-light);
  margin-right: auto;
}

body.dark-mode .support-message {
  background-color: #3a3a3a;
  color: var(--support-message-text-dark);
}

.chat-footer {
  display: flex;
  padding: 15px 20px;
  border-top: 1px solid var(--chat-input-border-light);
  background-color: var(--chat-footer-bg-light);
  transition: background-color 0.3s ease, border-top-color 0.3s ease;
}

body.dark-mode .chat-footer {
  background-color: var(--chat-footer-bg-dark);
  border-top-color: var(--chat-input-border-dark);
}

.chat-footer input {
  flex: 1;
  padding: 12px 15px;
  border: 1px solid var(--chat-input-border-light);
  border-radius: 20px;
  background-color: var(--chat-input-bg-light);
  color: var(--text-light);
  transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease;
}

body.dark-mode .chat-footer input {
  background-color: var(--chat-input-bg-dark);
  color: var(--text-dark);
  border-color: var(--chat-input-border-dark);
}

.chat-footer button {
  margin-left: 10px;
  padding: 12px 20px;
  background-color: var(--accent-light);
  color: white;
  border: none;
  border-radius: 20px;
  cursor: pointer;
  font-weight: 500;
  transition: background-color 0.3s ease;
}

body.dark-mode .chat-footer button {
  background-color: var(--accent-dark);
  color: var(--bg-dark);
}

.chat-footer button:hover {
  opacity: 0.9;
}

@media (max-width: 768px) {
  .content-container {
    padding: 30px 35px;
  }
  .content-container h1 {
    font-size: 2.4em;
  }
  .content-container p {
    font-size: 1.1em;
  }
  .highlight {
    font-size: 1.15em;
  }
  .chat-header .chat-title {
    max-width: calc(100% - 80px);
  }
  .main-page-chat-input-container {
    flex-direction: column;
    gap: 10px;
  }
  #main-page-chat-input {
    width: calc(100% - 36px); /* Considera padding */
    margin-right: 0;
  }
  #main-page-send-button {
    width: 100%;
    border-radius: 25px; /* Retangular em telas menores */
  }
}
@media (max-width: 480px) {
  .appsmith-widget-container {
    padding: 15px;
  }
  .dark-mode-toggle-container {
    top: 15px;
    right: 15px;
  }
  .dark-mode-toggle {
    padding: 8px 12px;
    font-size: 0.8em;
  }
  .icon {
    font-size: 1em;
  }
  .content-container h1 {
    font-size: 2em;
  }
  .content-container p {
    font-size: 1em;
  }
  .highlight {
    font-size: 1.05em;
  }
  .content-container {
    padding: 25px 20px;
  }
  .chat-header {
    padding: 15px;
    font-size: 16px;
  }
  .chat-header button#close-chat {
    font-size: 18px;
  }
  .chat-header .chat-title {
    max-width: calc(100% - 70px);
    font-size: inherit;
  }
  .messages-container {
    padding: 15px;
  }
  .message {
    padding: 6px 10px;
  }
  .chat-footer {
    padding: 10px 15px;
  }
  .chat-footer input {
    padding: 10px 12px;
  }
  .chat-footer button {
    padding: 10px 15px;
  }
}

