document.addEventListener('DOMContentLoaded', () => {
  const body = document.body;

  // --- L√≥gica do Bot√£o de Modo Escuro (Existente) ---
  const toggleContainer = document.createElement('div');
  toggleContainer.classList.add('dark-mode-toggle-container');
  const darkModeToggle = document.createElement('button');
  darkModeToggle.classList.add('dark-mode-toggle');
  darkModeToggle.setAttribute('aria-label', 'Alternar modo de cor');
  darkModeToggle.setAttribute('title', 'Alternar modo de cor');
  const toggleIcon = document.createElement('span');
  toggleIcon.classList.add('icon');
  const toggleText = document.createElement('span');
  toggleText.classList.add('toggle-text');
  darkModeToggle.appendChild(toggleIcon);
  darkModeToggle.appendChild(toggleText);
  toggleContainer.appendChild(darkModeToggle);
  body.appendChild(toggleContainer);
  const prefersDarkScheme = window.matchMedia("(prefers-color-scheme: dark)");
  function setTheme(isDark) {
    body.classList.toggle('dark-mode', isDark);
    if (toggleText) toggleText.textContent = isDark ? 'Claro' : 'Escuro';
    if (toggleIcon) toggleIcon.textContent = isDark ? '‚òÄÔ∏è' : 'üåô';
    try {
      localStorage.setItem('theme', isDark ? 'dark' : 'light');
    } catch (e) {
      console.error("N√£o foi poss√≠vel acessar o localStorage:", e);
    }
  }
  let savedTheme = null;
  try {
    savedTheme = localStorage.getItem('theme');
  } catch (e) {/* Silencioso */}
  if (savedTheme) {
    setTheme(savedTheme === 'dark');
  } else {
    setTheme(prefersDarkScheme.matches);
  }
  darkModeToggle.addEventListener('click', () => {
    setTheme(!body.classList.contains('dark-mode'));
  });
  prefersDarkScheme.addEventListener('change', e => {
    let currentSavedTheme = null;
    try {
      currentSavedTheme = localStorage.getItem('theme');
    } catch (error) {/* Silencioso */}
    if (!currentSavedTheme) {
      setTheme(e.matches);
    }
  });
  // --- Fim da L√≥gica do Modo Escuro ---

  // --- L√≥gica do Chat Widget (Existente e Nova) ---
  const chatBubble = document.getElementById("chat-bubble");
  const chatWidget = document.getElementById("chat-widget");
  const closeChatButton = document.getElementById("close-chat");
  const messagesContainer = document.getElementById("messages-container");
  const chatInput = document.getElementById("chat-input"); // Input dentro do chat widget
  const sendMessageButton = document.getElementById("send-message"); // Bot√£o dentro do chat widget

  // Novos elementos para o input na p√°gina principal
  const mainPageChatInput = document.getElementById("main-page-chat-input");
  const mainPageSendButton = document.getElementById("main-page-send-button");
  if (chatBubble && chatWidget && closeChatButton) {
    chatBubble.addEventListener("click", () => {
      openChatWidget();
    });
    closeChatButton.addEventListener("click", () => {
      closeChatWidget();
    });
  } else {
    console.error("Elementos do chat (bubble, widget, ou bot√£o de fechar) n√£o encontrados.");
  }
  if (sendMessageButton && chatInput && messagesContainer) {
    // Para o chat interno
    sendMessageButton.addEventListener("click", sendChatMessage);
    chatInput.addEventListener("keypress", e => {
      if (e.key === "Enter") {
        sendChatMessage();
        e.preventDefault();
      }
    });
  } else {
    console.error("Elementos de envio de mensagem do chat (bot√£o, input ou container) n√£o encontrados.");
  }

  // L√≥gica para o novo input na p√°gina principal
  if (mainPageChatInput && mainPageSendButton) {
    mainPageSendButton.addEventListener("click", sendMessageFromMainPage);
    mainPageChatInput.addEventListener("keypress", e => {
      if (e.key === "Enter") {
        sendMessageFromMainPage();
        e.preventDefault();
      }
    });
  } else {
    console.error("Elementos do input de chat da p√°gina principal n√£o encontrados.");
  }
  function openChatWidget() {
    if (chatWidget) chatWidget.style.display = "flex";
    if (chatBubble) chatBubble.style.display = "none";
  }
  function closeChatWidget() {
    if (chatWidget) chatWidget.style.display = "none";
    if (chatBubble) chatBubble.style.display = "flex";
  }
  function sendChatMessage() {
    // Envia mensagem do input DENTRO do chat
    const userMessage = chatInput.value.trim();
    if (!userMessage) return;
    processAndSendMessage(userMessage);
    chatInput.value = "";
  }
  function sendMessageFromMainPage() {
    // Envia mensagem do NOVO input na p√°gina principal
    const userMessage = mainPageChatInput.value.trim();
    if (!userMessage) return;
    processAndSendMessage(userMessage);
    mainPageChatInput.value = "";
    openChatWidget(); // Abre o chat ap√≥s enviar a mensagem
    // Opcional: focar no input do chat ap√≥s abrir
    // if (chatInput) chatInput.focus(); 
  }
  function processAndSendMessage(message) {
    addMessageToUI(message, true); // Adiciona √† UI do chat
    // Envia para o backend do Appsmith
    if (typeof appsmith !== 'undefined' && typeof appsmith.triggerEvent === 'function') {
      appsmith.triggerEvent('enviar', {
        msg: message
      });
    } else {
      console.warn("Appsmith SDK n√£o est√° dispon√≠vel ou triggerEvent n√£o √© uma fun√ß√£o. Mensagem n√£o enviada para o backend.");
      // Fallback para teste local
      setTimeout(() => {
        addMessageToUI("Sou um bot de teste. Appsmith n√£o detectado. Sua mensagem: " + message, false);
      }, 1000);
    }
  }
  function addMessageToUI(message, isUser) {
    if (!messagesContainer) return;
    const messageElement = document.createElement("div");
    messageElement.className = "message " + (isUser ? "user-message" : "support-message");
    messageElement.textContent = message;
    messagesContainer.appendChild(messageElement);
    messagesContainer.scrollTop = messagesContainer.scrollHeight;
  }
  function updateMessagesFromAppsmith() {
    if (typeof appsmith === 'undefined' || typeof appsmith.model === 'undefined') {
      return;
    }
    let chatHistory = appsmith.model.chatHistory || [];
    if (!chatHistory.length) return;
    const lastMessageInHistory = chatHistory[chatHistory.length - 1];
    const lastMessageInUI = messagesContainer.lastElementChild;
    if (lastMessageInHistory && lastMessageInHistory.role === "CHATBOT") {
      if (!lastMessageInUI || !lastMessageInUI.classList.contains("support-message") || lastMessageInUI.textContent !== lastMessageInHistory.message) {
        addMessageToUI(lastMessageInHistory.message, false);
      }
    }
  }
  if (typeof appsmith !== 'undefined' && typeof appsmith.onReady === 'function') {
    appsmith.onReady(() => {
      if (typeof appsmith.onModelChange === 'function') {
        if (messagesContainer) messagesContainer.innerHTML = '';
        updateMessagesFromAppsmith();
        appsmith.onModelChange(updateMessagesFromAppsmith);
      } else {
        console.warn("appsmith.onModelChange n√£o √© uma fun√ß√£o.");
      }
    });
  } else {
    console.warn("Appsmith SDK n√£o est√° dispon√≠vel. Funcionalidades do chat ligadas ao Appsmith podem n√£o funcionar.");
  }
  // --- Fim da L√≥gica do Chat Widget ---
});

